mapper.update:
update SYS_USER set 
SKIN = #{SKIN} 
where USERNAME = #{USERNAME}

update SYS_USER set 
LAST_LOGIN = #{LAST_LOGIN} 
where USER_ID = #{USER_ID}

mapper.select:
select * from SYS_USER where 1=1
and ( (USERNAME = #{USERNAME} and PASSWORD = #{PASSWORD}) or (EMAIL = #{USERNAME} and PASSWORD = #{PASSWORD}) or (NUMBER = #{USERNAME} and PASSWORD = #{PASSWORD}) )
and USER_ID = #{USER_ID}

mapper.update:
update SYS_USER 
	set 
		IP = #{IP}
	where 
		USERNAME = #{USERNAME}

update SYS_USER set USERNAME = #{USERNAME}, NAME = #{NAME}, ROLE_ID = #{ROLE_ID}, BZ = #{BZ}, EMAIL = #{EMAIL}, NUMBER = #{NUMBER}, PHONE = #{PHONE}
,PASSWORD = #{PASSWORD}
where USER_ID = #{USER_ID}

mapper.select:
select u.USER_ID, u.USERNAME, u.PASSWORD, u.LAST_LOGIN, u.NAME, u.IP, u.EMAIL, u.NUMBER, u.PHONE, r.ROLE_ID, c.NAME AS BM_NAME, r.ROLE_NAME from SYS_USER u, SYS_ROLE r ,SYS_DICTIONARIES c where u.ROLE_ID = r.ROLE_ID and u.ZD_ID = c.ZD_ID and u.USERNAME != 'admin' and (r.ROLE_ID != '7' and r.PARENT_ID != '7')
and ( u.USERNAME LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') or u.EMAIL LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') or u.NUMBER LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') or u.NAME LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') or u.PHONE LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') )
and (u.ZD_ID=#{pd.ZD_ID} or c.PARENT_ID=#{pd.ZD_ID})
and u.ROLE_ID=#{pd.ROLE_ID}
and u.LAST_LOGIN >= #{pd.lastLoginStart}
and u.LAST_LOGIN <= #{pd.lastLoginEnd}
order by u.LAST_LOGIN desc

select u.USER_ID, u.USERNAME, u.PASSWORD, u.LAST_LOGIN, u.NAME, u.IP, u.EMAIL, u.NUMBER, u.PHONE, r.ROLE_ID, c.NAME AS BM_NAME, r.ROLE_NAME, au.user_id as au_user_id FROM SYS_USER u LEFT JOIN SYS_ROLE r ON u.ROLE_ID = r.ROLE_ID LEFT JOIN SYS_DICTIONARIES c ON u.ZD_ID = c.ZD_ID LEFT JOIN zc_anchor_user au ON u.USER_ID=au.user_id AND au.anchor_id=#{pd.anchor_id} WHERE u.USERNAME != 'admin' and (r.ROLE_ID != '7' and r.PARENT_ID != '7')
and ( u.USERNAME LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') or u.EMAIL LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') or u.NUMBER LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') or u.NAME LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') or u.PHONE LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') )
and (u.ZD_ID=#{pd.ZD_ID} or c.PARENT_ID=#{pd.ZD_ID})
and u.ROLE_ID=#{pd.ROLE_ID}
and u.LAST_LOGIN >= #{pd.lastLoginStart}
and u.LAST_LOGIN <= #{pd.lastLoginEnd}
order by u.LAST_LOGIN desc

select u.USER_ID, u.USERNAME, u.PASSWORD, u.LAST_LOGIN, u.NAME, u.IP, u.EMAIL, u.NUMBER, u.PHONE, r.ROLE_ID, c.NAME AS BM_NAME, r.ROLE_NAME, tu.user_id AS tu_user_id FROM SYS_USER u LEFT JOIN SYS_ROLE r ON u.ROLE_ID = r.ROLE_ID LEFT JOIN SYS_DICTIONARIES c ON u.ZD_ID = c.ZD_ID LEFT JOIN zc_tag_user tu ON u.USER_ID=tu.user_id AND tu.tag_id=#{pd.tag_id} WHERE u.USERNAME != 'admin' and (r.ROLE_ID != '7' and r.PARENT_ID != '7')
and ( u.USERNAME LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') or u.EMAIL LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') or u.NUMBER LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') or u.NAME LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') or u.PHONE LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%') )
and (u.ZD_ID=#{pd.ZD_ID} or c.PARENT_ID=#{pd.ZD_ID})
and u.ROLE_ID=#{pd.ROLE_ID}
and u.LAST_LOGIN >= #{pd.lastLoginStart}
and u.LAST_LOGIN <= #{pd.lastLoginEnd}
order by u.LAST_LOGIN desc

select u.USER_ID, u.USERNAME, u.PASSWORD, u.LAST_LOGIN, u.NAME, u.IP, u.EMAIL, u.NUMBER, u.PHONE, r.ROLE_ID, c.NAME AS BM_NAME, r.ROLE_NAME from SYS_USER u, SYS_ROLE r ,SYS_DICTIONARIES c where u.ROLE_ID = r.ROLE_ID and u.ZD_ID = c.ZD_ID and u.USERNAME != 'admin' and (r.ROLE_ID != '7' and r.PARENT_ID != '7')
and ( u.USERNAME LIKE CONCAT(CONCAT('%', #{USERNAME}),'%') or u.EMAIL LIKE CONCAT(CONCAT('%', #{USERNAME}),'%') or u.NUMBER LIKE CONCAT(CONCAT('%', #{USERNAME}),'%') or u.NAME LIKE CONCAT(CONCAT('%', #{USERNAME}),'%') or u.PHONE LIKE CONCAT(CONCAT('%', #{USERNAME}),'%') )
and u.ROLE_ID=#{ROLE_ID}
and u.LAST_LOGIN >= #{lastLoginStart}
and u.LAST_LOGIN <= #{lastLoginEnd}
order by u.LAST_LOGIN desc
